changeSet.sql:
CREATE TABLE pending_action ( id bigint NOT NULL, version INT NOT NULL, changed_entity_id BIGINT NOT NULL, saved_entity_type VARCHAR(255) NULL, saved_entity JSON NULL, deleted_key BIGINT NULL, retry_count INT NOT NULL,
created_time TIMESTAMP NOT NULL, created_by VARCHAR(64) NOT NULL, modified_time TIMESTAMP, modified_by VARCHAR(64), deleted BOOLEAN NOT NULL,
PRIMARY KEY(id) );
