changeSet.sql:
create table email_history( id bigint not null, user_id bigint, source varchar(64) not null, action varchar(64) not null, locale varchar(32), type smallint, payload varchar(4000), recipient varchar(512), status smallint, status_reason varchar(256), retry_count smallint, priority smallint, sent_date date, is_resend boolean,
created_by varchar(32), created_time timestamp, modified_by varchar(32), modified_time timestamp,
primary key(id) );
create index email_history_user_id_idx on email_history (user_id);
create table email_schedule( id bigint not null, user_id bigint, source varchar(64), action varchar(64), locale varchar(64), payload varchar(4000), recipient varchar(512), priority smallint, schedule_date date,
created_by varchar(32), created_time timestamp, modified_by varchar(32), modified_time timestamp,
primary key(id) );
create index email_schedule_schedule_date_idx on email_schedule (schedule_date);
