create table payment_instrument_type ( payment_instrument_type_id smallint not null, name varchar(32) not null, recurring varchar(16) null, refundable varchar(16) null, authorizable varchar(16) null, defaultable varchar(16) null, created_time date not null, created_by varchar(50) not null,
constraint payment_instrument_type_id_pk primary key (payment_instrument_type_id) );
create table credit_card_type ( credit_card_type_id smallint not null, description varchar(20) not null, display_name varchar not null, created_time date not null, created_by varchar(50) null,
constraint credit_card_type_id_pk primary key (credit_card_type_id) );
create table address ( address_id bigint not null, unit_number varchar(10) null, address_line_1 varchar(100) not null, address_line_2 char(100) null, address_line_3 varchar(100) null, city varchar(50) null, district char(50) null, state varchar(50) null, country_code char(3) not null, postal_code varchar(50) not null, created_time date not null, created_by varchar(50) null, updated_time date null, updated_by varchar(50) null,
constraint address_id_pk primary key (address_id) );
create table payment_instrument ( payment_instrument_id bigint not null, user_id bigint not null, payment_instrument_type_id smallint not null, payment_instrument_holder_name varchar(100) null, encrypted_account_number varchar(200) null, account_number varchar(100) null, revision int not null, address_id bigint null, phone_num varchar(64) null, email varchar(64) null, relation_to_holder varchar(32) null, active boolean not null, deleted boolean null, last_validated_time date null, external_token varchar(64) null, created_time date not null, created_by varchar(50) null, updated_time date null, updated_by varchar(50) null,
constraint payment_instrument_id_pk primary key (payment_instrument_id), constraint address_id_fk foreign key (address_id) references address (address_id) match simple on update no action on delete no action, constraint payment_instrument_type_id_fk foreign key (payment_instrument_type_id) references payment_instrument_type (payment_instrument_type_id) match simple on update no action on delete no action );
create table credit_card_payment_instrument ( payment_instrument_id bigint not null, credit_card_type_id smallint not null, expire_time varchar(16) not null, last_billing_time date not null, prepaid varchar(16) null, debit varchar(16) null, commercial varchar(16) null, issue_country varchar(16) null, created_time date not null, created_by varchar(50) null, updated_time date null, updated_by varchar(50) null,
constraint cc_payment_instrument_id_pk primary key (payment_instrument_id), constraint credit_card_type_id_fk foreign key (credit_card_type_id) references credit_card_type (credit_card_type_id) match simple on update no action on delete no action );
create table payment_provider ( payment_provider_id smallint not null, payment_provider_name varchar(50) not null, payment_instrument_type_id smallint not null, country_code char(3) null, currency_code char(3) null, created_time date not null, created_by varchar(50) not null,
constraint payment_provider_id_pk primary key (payment_provider_id) );
create table tenant ( tenant_id int not null, name varchar(128) not null, description varchar(1024) not null, created_time date not null, created_by varchar(64) not null,
constraint tenant_id_pk primary key (tenant_id) );
create table merchant_account ( merchant_account_id int not null, merchant_account_ref varchar(50) not null, tenant_id int null, payment_provider_id smallint not null, payment_instrument_type_id smallint null, country_code char(3) null, currency_code char(3) not null, created_time date not null, created_by varchar(50) null,
constraint merchant_account_id_pk primary key (merchant_account_id) );
create table payment_type ( payment_type_id smallint not null, name varchar(128) not null, description varchar(1024) not null, created_time date not null, created_by varchar(64) not null,
constraint payment_type_id_pk primary key (payment_type_id) );
create table payment_event_type ( payment_event_type_id smallint not null, name varchar(128) not null, description varchar(1024) not null, created_time date not null, created_by varchar(64) not null,
constraint payment_event_type_id_pk primary key (payment_event_type_id) );
create table payment_status ( payment_status_id smallint not null, name varchar(128) not null, description varchar(1024) not null, created_time date not null, created_by varchar(64) not null,
constraint payment_status_id_pk primary key (payment_status_id) );
create table payment ( payment_id bigint not null, user_id bigint not null, payment_type_id smallint not null, currency_code char(3) not null, net_amount numeric not null, country_code char(3) null, business_descriptor varchar(50) null, payment_instrument_id bigint not null, payment_provider_id int not null, merchant_account_id int null, payment_status_id smallint not null, external_token varchar(128) null, billing_ref_id varchar(128) not null, created_time date not null, created_by varchar(50) null, updated_time date null, updated_by varchar(50) null,
constraint payment_id_pk primary key (payment_id),
constraint merchant_account_id_fk foreign key (merchant_account_id) references merchant_account (merchant_account_id) match simple on update no action on delete no action, constraint payment_provider_id_fk foreign key (payment_provider_id) references payment_provider (payment_provider_id) match simple on update no action on delete no action, constraint payment_status_id_fk foreign key (payment_status_id) references payment_status (payment_status_id) match simple on update no action on delete no action, constraint payment_type_id_fk foreign key (payment_type_id) references payment_type (payment_type_id) match simple on update no action on delete no action );
create table payment_event ( payment_event_id bigint not null, payment_id bigint not null, payment_event_type_id smallint not null, event_status_id smallint not null, currency_code char(3) null, net_amount numeric null, request json not null, response json not null, created_time date not null, created_by varchar(50) null, updated_time date null, updated_by varchar(50) null,
constraint payment_event_id_pk primary key (payment_event_id), constraint payment_id_fk foreign key (payment_id) references payment (payment_id) match simple on update no action on delete no action, constraint event_status_id_fk foreign key (event_status_id) references payment_status (payment_status_id) match simple on update no action on delete no action, constraint payment_event_type_id_fk foreign key (payment_event_type_id) references payment_event_type (payment_event_type_id) match simple on update no action on delete no action );
